import styled from "styled-components";




    const Button = styled.button` 
    
  font: inherit;
 
  border-radius: 10px;
  background: #4f005f;
  color: white;
  padding: 0.25rem 1rem;
  cursor: pointer;


&:hover,
&:active {
  background: #741188;
  border-color: #741188;
}

&:focus {
  outline: none;
}`
    

 

export default Button;
