import Card from "./Card";
import Button from "./Button";
import styled from "styled-components";

const ModalComponent=styled.ModalComponent` 
&:backdrop {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  z-index: 10;
  background: rgba(0, 0, 0, 0.75);
}

&:modal {
    position: fixed ;
  top: 30vh;
  left: 10%;
  width: 80%;
  z-index: 100;
  overflow: hidden;
}

&:header {
  background: #4f005f;
  padding: 1rem;
}

.header h2 {
  margin: 0;
  color: white;
}

.content {
  padding: 1rem;
}

.footer {
  padding: 1rem;
  display: flex;
  justify-content: flex-end;
}

@media (min-width: 768px) {
  .modal {
    left: calc(50% - 20rem);
    width: 40rem;
  }
}

 ` 


function ErrorModal(props){
    return (
        <div>
        <div className="backdrop"/>
        
<Card className="modal">
<ModalComponent>
    <header >
        <h2>{props.Title}</h2>
    </header>
    <content>
    <p>{props.Massage}</p>
    </content>
    <footer>
        <Button>Okay</Button>
    </footer>
    </ModalComponent>
</Card>
 
 </div>
    )
}

export default ErrorModal;
