import styled from "styled-components";

function Card(props){
    const CardDiv = styled.div`
    background-color: white;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.26);
    border-radius: 10px;
    
    &.input {
  margin: 2rem auto;
  padding: 1rem;
  width: 90%;
  max-width: 40rem;
}

&.input label {
  display: block;
  font-weight: bold;
  margin-bottom: 0.5rem;
}

&.input input {
  font: inherit;
  display: block;
  width: 100%;
  border: 1px solid #ccc;
  padding: 0.15rem;
  margin-bottom: 0.5rem;
}

&input:focus {
  outline: none;
  border-color: #4f005f;
}`

    return (
<CardDiv> {props.children}</CardDiv>
    )
}


export default Card;
